mapper.select:
select

    DATE_FORMAT(accept_time, '%m') as month,


    DATE_FORMAT(finish_time, '%m') as month,

    count(1) as count
from
    ols_accept
where
    user_id = #{userId}

    and DATE_FORMAT(accept_time, '%Y')=#{year}


    and DATE_FORMAT(finish_time, '%Y')=#{year}

    and state = #{state}
    and type=1
GROUP BY

    DATE_FORMAT(accept_time, '%m');


    DATE_FORMAT(finish_time, '%m');

select

    DATE_FORMAT(accept_time, '%m') as month,


    DATE_FORMAT(ols_accept.finish_time, '%m') as month,

count(1) as count
from
ols_accept,ols_task
where
user_id = #{userId}

    and DATE_FORMAT(accept_time, '%Y')=#{year}


    and DATE_FORMAT(ols_accept.finish_time, '%Y')=#{year}

and ols_accept.state = #{state}
and ols_task.type = 0
and ols_task.id=ols_accept.task_id
GROUP BY

    DATE_FORMAT(accept_time, '%m');


    DATE_FORMAT(ols_accept.finish_time, '%m');

select
    SQL_CALC_FOUND_ROWS id,
    user_id,task_id,accept_time,finish_time,state,url
from
    ols_accept
where
    task_id=#{taskId}

    limit
    #{start},#{end};


    ;

SELECT FOUND_ROWS() as total;

mapper.update:
update
    ols_accept
set
    state=#{state}
where
    id=#{id}
